$(document).ready(function () {

    $('[data-bs-toggle="tooltip"]').tooltip();

  $(".sidebar-dropdown > a").on("click", function(e) {
      e.preventDefault(); // Prevenir la redirección
      
      var $submenu = $(this).next(".sidebar-submenu");

      // Cierra otros submenús abiertos
      $(".sidebar-submenu").not($submenu).slideUp(200);
      $(".sidebar-dropdown").not($(this).parent()).removeClass("active");

      // Despliega el submenú del elemento actual
      $submenu.slideToggle(200);
      $(this).parent().toggleClass("active");
  });

  // Muestra y oculta el menú lateral
  $("#close-sidebar").click(function() {
      $(".page-wrapper").removeClass("toggled");
  });
  
  $("#show-sidebar").click(function() {
      $(".page-wrapper").addClass("toggled");
  });
});

  // Configura las opciones globales de toastr
  toastr.options = {
    "closeButton": true, // Muestra un botón de cerrar
    "debug": false,
    "newestOnTop": true, // Las notificaciones más nuevas están en la parte superior
    "progressBar": true, // Muestra una barra de progreso
    //"positionClass": "toast-bottom-right", // Posición en la parte superior derecha
    "preventDuplicates": false,
    "onclick": null,
    "showDuration": "300", // Duración de la animación de aparición
    "hideDuration": "1000", // Duración de la animación de desaparición
    "timeOut": "5000", // Tiempo en milisegundos que se muestra la notificación
    "extendedTimeOut": "1000", // Tiempo adicional de visibilidad en hover
    "showEasing": "swing",
    "hideEasing": "linear",
    "showMethod": "fadeIn", // Método de aparición
    "hideMethod": "fadeOut" // Método de desaparición
  };
